mapper.select:
select * from cash_item where deleted = 0

    and projectId = #{input.projectId}


    and cashUserName like concat('%',#{input.cashUserName},'%')


    and projectName like concat('%',#{input.projectName},'%')


    and projectDate >= #{input.startProjectDate}


    and projectDate <= #{input.startProjectDate}

order by  createTime desc
